package com.syntax.class01;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//8 primitive datatypes
		
		//to store whole numbers --> byte, short, int, long
		
		byte variableOne=10;
		short variable2=100;
		int variableThree=1000;
		long variable_four=100000;
		
		//to store decimal numbers --> float, double 
		
		float variable$Five=12.99f;
		double anyName=132.89;
		
		//to store single character --> char
		
		char singleValue='F';
		char anotherVariable='%';
		
		//to store boolean value --> boolean 
		
		boolean hungry=true;
		boolean thirsty=false; 
		
		System.out.println(anotherVariable);
		
		System.out.println(anyName);
		System.out.println(variable$Five);
		

	}

}
